  community_description = #{communityDescription,jdbcType=VARCHAR},
  community_url = #{communityUrl,jdbcType=VARCHAR},
  community_image_url = #{communityImageUrl,jdbcType=VARCHAR}
where community_id = #{communityId,jdbcType=CHAR}
